import styled from "styled-components";
import {useDispatch,useSelector} from 'react-redux';
import { useNavigate } from "react-router-dom";
import { auth, provider } from "./firebase";
import { signInWithPopup } from "firebase/auth";
import {
    selectUserName,
    selectUserEmail,
    selectUserPhoto,
    setUserLoginDetails,
    setUserSignoutState
} from '../features/users/userSlice';
import { useEffect } from "react";
import { Link } from "react-router-dom";

const Header=(props)=>{
    const dispatch=useDispatch();
    const history=useNavigate();
    const username=useSelector(selectUserName);
    const userPhoto=useSelector(selectUserPhoto);
    
    const navigate=useNavigate();

    useEffect(()=>{
        auth.onAuthStateChanged(async (user)=>{
            if(user){
                setUser(user);
                navigate("/home");
            }
        })
     },[username] )

    const handleAuth=()=>{
        if(!username){
        signInWithPopup(auth,provider).then((result)=>{
            setUser(result.user);
        }).catch((error)=>{
            alert(error.message);
        })
    }else{
        auth.signOut().then(()=>{
            dispatch(setUserSignoutState());
            navigate("/");
        }).catch((err)=>alert(err.message));
    }
    };

    const setUser = (user)=>{
        dispatch(
           setUserLoginDetails({
            name:user.displayName,
            email:user.email,
            photo:user.photoURL,
           }

           ) 
        )
    }
    return(
     <Nav>
        <Logo>
            <Link to="/home">
            <img src="/Images/logo.svg" alt="Disney+"></img>
            </Link>
            
        </Logo>

        {
            !username ?
            <Login onClick={handleAuth}>Login</Login> : 
            <>
              <NavMenu>
            <a href='/home'>
                <img src="Images/home-icon.svg"></img>
                <span>HOME</span>
            </a>
            <a href='/home'>
                <img src="Images/search-icon.svg"></img>
                <span>SEARCH</span>
            </a>
            <a href='/home'>
                <img src="Images/watchlist-icon.svg"></img>
                <span>WATCHLIST</span>
            </a>
            <a href='/home'>
                <img src="Images/original-icon.svg"></img>
                <span>ORIGINALS</span>
            </a>
            <a href='/home'>
                <img src="Images/movie-icon.svg"></img>
                <span>MOVIES</span>
            </a>
            <a href='/home'>
                <img src="Images/series-icon.svg"></img>
                <span>SERIES</span>
            </a>
        </NavMenu>
        
        <SignOut>
        <UserImg src={userPhoto} alt={username}></UserImg>
        <Dropdown>
            <span onClick={handleAuth}>Sign out</span>
        </Dropdown>
        </SignOut>

            </>
        }
        
     </Nav>
    )
}

const Nav = styled.nav`
  position: fixed;
  top:0;
  left: 0;
  right:0;
  height: 70px;
  background-color: #090b13;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding:0 36px;
  letter-spacing: 16px;
  z-index: 3;

`
const Logo=styled.a`
 padding:0;
 width:80px;
 margin-top: 4px;
 font-size: 0;
 display:inline-block;

 img{
    display: block;
    width: 100%;
 } 
`
const NavMenu=styled.div`
    align-items:center;
    display:flex;
    flex-flow:row nowrap;
    height:100%;
    justify-content: flex-end;
    margin:0px;
    padding:0px;
    position:relative;
    margin-right: auto;
    margin-left: 25px;


    @media(max-width: 768px){
        display:none;
    }

    a{
        display:flex;
        align-items: center;
        padding: 0 12px;
    img{
        height: 20px;
        min-width: 10px;
        width:20px;
        z-index: auto;
    }

    span{
        color: rgb(249,249,249);
        font-size: 13px;
        letter-spacing: 1.42px;
        line-height:1.08;
        padding:2px 0px;
        white-space: nowrap;
        position:relative;

    &:before {
        background-color:rgb(249,249,249) ;
        border-radius:0px 0px 4px 4px;
        bottom:-6px;
        content: "";
        height: 2px;
        left:0px;
        opacity: 0;
        position: absolute;
        right: 0px;
        transform-origin: left center;
        transform: scaleX(0);
        transition: all 250ms cubic-bezier(0.25,0.46,0.45,0.94) 0s;
        visibility:hidden;
        width: auto;
    }
  
    }
    &:hover{
        span:before{
           transform :scaleX(1) ;
           visibility: visible;
           opacity: 1 !important;
        }
    }
}
    
`

const Login=styled.a`
  background-color:rgba(0,0,0,0.6);
  padding:8px 16px;
  text-transform: uppercase;
  letter-spacing: 1.5px;
  border: 1px solid white;
  border-radius: 4px;
  transition:all 200ms ease-out;
  cursor: pointer;
  
  &:hover{
    background-color:#f9f9f9;
    color:#000;
    border-color: transparent;
    /* cursor:pointer;  */
  }
`
const UserImg=styled.img`
   height:100%;
`

const Dropdown=styled.div`
  position: absolute;
  top: 48px;
  right: 0px;
  background-color:rgb(19,19,19);
   border: 1px solid rgba(151,151,151,0.34);
   border-radius: 4px;
   box-shadow: rgb(0 0 0 /50%) 0px 0px 18px 0px;
   padding: 10px;
   font-size: 14px;
   letter-spacing: 3px;
   width:100px;
   opacity: 0;
`

const SignOut=styled.div`
  position: relative;
  height: 48px;
  width:48px;
  display:flex;
  cursor:pointer;
  align-items: center;
  justify-content: center ;

  ${UserImg}{
    border-radius: 50%;
    width: 100%;
    height:100%;
  }

  &:hover{
    ${Dropdown}{
        opacity:1;
        transition-duration: 1s;
    }
  }
`

export default Header;
